  /**
   * Solution for the fifth task.
   */
  public void workingWithArrays() {
    int[] array = {5, 2, 6, 4, 1, 3, 7, 9, 8};
    // Create an instance of the class arry
    ArrayAlgorithms arrayAlgorithms = new ArrayAlgorithms();

    // Calculate the avrg of the array and print it out
    double arrayAverage =  arrayAlgorithms.average(array);
    System.out.println("Avrg: " +  arrayAverage);

    int arrayMaxValue = arrayAlgorithms.maxValue(array);
    System.out.println("Largest value is: " +  arrayMaxValue);

    int arrayMininIndex = arrayAlgorithms.minIndex(array);
    System.out.println("Index of smallest int is at: " +  arrayMininIndex);


    int[] shuffledArray = arrayAlgorithms.shuffle(array);
    System.out.println("Shuffled array: ");
    
    // Iterate through each element of the shuffled array and print the current element
    for (int i = 0; i < shuffledArray.length; i++) {
      System.out.print(shuffledArray[i]);

      // If it's not the last element, print a comma and space
      if (i != shuffledArray.length - 1) {
        System.out.print(", ");

      }
    }

  }

